INSERT INTO public.car (id, car_name, category, description, icon_name, in_rent, number_of_rents, price) VALUES (17, 'Aston Martin Vantage', 'supercar', 'Aston Martin является типичным представителем мелкосерийного производства британских спортивных автомобилей для гранд-туризма, занимающим положение примерно между Jaguar и Bentley и примером которого является DB5, прославившийся благодаря поездке Джеймса Бонда в фильмах « Голдфингер » ', 'vantage', false, 0, 105000);
INSERT INTO public.car (id, car_name, category, description, icon_name, in_rent, number_of_rents, price) VALUES (1, 'Dodge Charger', 'american_muscle', 'Один из наиболее брутальных масклкаров 1970 года, да и вообще за всю историю! Этот Додж, не блещет плавностью линий или эстетикой, он груб и как будто, создан для кузовных гонок, типа NASCAR. С закрытыми фарами - неотразим и крут!', 'charger', false, 0, 15000);
INSERT INTO public.car (id, car_name, category, description, icon_name, in_rent, number_of_rents, price) VALUES (2, 'Chevy Corvette', 'american_muscle', 'Двухместный заднеприводный спорткаром, выпускаемый компанией Chevrolet дочерней коорпорации General Motors, США с 1953 года. Автомобиль является первым американским спортивным автомобилем, созданным американским производителем.', 'corvette', false, 0, 25000);
INSERT INTO public.car (id, car_name, category, description, icon_name, in_rent, number_of_rents, price) VALUES (3, 'Pontiac Firebird', 'american_muscle', 'Дизайн первого поколения Firebird походил на бутылку «кока-колы». В отличие от Chevrolet Camaro, у Firebird бампера были интегрированы в дизайн передка, а задние «рубленые» фары были позаимствованы из дизайна Pontiac GTO.', 'firebird', false, 0, 14000);
INSERT INTO public.car (id, car_name, category, description, icon_name, in_rent, number_of_rents, price) VALUES (4, 'Dodge Challenger', 'american_muscle', 'Многие считают Challenger одним из самых красивых muscle cars, когда-либо построенных. Однако то, что скрывается под капотом, отличает этого красавца. Легендарный двигатель 440 Six Pack, 4-ступенчатая МКПП А833, Super Track Pak, гидроусилитель руля, усилитель передних тормозов, пакет освещения, гоночные приборы, спортивный капот.', 'challenger', false, 0, 16000);
INSERT INTO public.car (id, car_name, category, description, icon_name, in_rent, number_of_rents, price) VALUES (6, 'Ford Mustang', 'american_muscle', 'Это автомобиль, о котором все думают, когда думают о настоящем маслкаре 1970-х годов.', 'mustang', false, 0, 30000);
INSERT INTO public.car (id, car_name, category, description, icon_name, in_rent, number_of_rents, price) VALUES (7, 'Porsche 911 BlackBird', 'sportcar', 'Blackbird Porsche напрямую вдохновлен 930 Turbo Йошиды, который также был черным, когда он его получил, а позже покрасил его в темно-фиолетовый цвет.', '911mnc', false, 0, 20000);
INSERT INTO public.car (id, car_name, category, description, icon_name, in_rent, number_of_rents, price) VALUES (10, 'Mercedes C-AMG', 'sportcar', 'Сейчас Mercedes C63 AMG Black Series способен разогнаться с нуля до первой «сотни» всего за 3,6 секунды, а это на 0,5 секунды быстрее, чем было прежде. Также до 350-ти км/час увеличилась максимальная скорость купе. ', 'w204', false, 0, 30000);
INSERT INTO public.car (id, car_name, category, description, icon_name, in_rent, number_of_rents, price) VALUES (11, 'BMW M3 Gt-S', 'sportcar', 'M3 e92 считался одним из лучших в мире спорткаров, который на шоссе соперничал со многими суперкарами', 'e92gts', false, 0, 32000);
INSERT INTO public.car (id, car_name, category, description, icon_name, in_rent, number_of_rents, price) VALUES (12, 'Dodge Hellcat', 'sportcar', 'Шины Dodge Challenger SRT Hellcat умирают. Это одно из последних купе с V-8 Thunder в американском стиле под капотом. Самая сдержанная модель линейки Challenger SRT Hellcat выдает 717 лошадиных сил от своего 6,2-литрового двигателя V-8 с наддувом. ', 'hellcat', false, 0, 25000);
INSERT INTO public.car (id, car_name, category, description, icon_name, in_rent, number_of_rents, price) VALUES (8, 'Mitsubishi Evolution', 'sportcar', 'Для восьмой серии раллийной франшизы инженеры "Mitsubishi" повысили ей управляемость, и это при помощи своей системы Super Active Yaw Control. Еще одна обновка в машине, пришедшая на Evolution, заключалась в использовании новейшей 6-скоростной ручной (механической) КПП.', 'evo', false, 1, 12000);
INSERT INTO public.car (id, car_name, category, description, icon_name, in_rent, number_of_rents, price) VALUES (13, 'Porsche GT2RS', 'supercar', 'Porsche 911 GT2 RS года в настоящее время находится на вершине иерархии производительности легендарного 911. Его сильно вентилируемый кузов и заметное заднее антикрыло помогают определить, что также является самым мощным серийным 911, когда-либо созданным Porsche.', '911gt2rs', false, 0, 60000);
INSERT INTO public.car (id, car_name, category, description, icon_name, in_rent, number_of_rents, price) VALUES (14, 'Lamborghini Aventador', 'supercar', 'С хриплым двигателем V-12 мощностью 769 л.с. и экзотическим внешним видом Lamborghini Aventador 2022 года — это определение гиперкара.', 'aventador', false, 0, 80000);
INSERT INTO public.car (id, car_name, category, description, icon_name, in_rent, number_of_rents, price) VALUES (15, 'Ferrari 458', 'supercar', 'Ferrari 458 — это воплощение мечты о суперкарах. Широкоплечий, с выпуклыми крыльями и изящным сводом крыши, этот уверенный в себе итальянец выделяется из толпы.', 'ferrari458', false, 0, 85000);
INSERT INTO public.car (id, car_name, category, description, icon_name, in_rent, number_of_rents, price) VALUES (16, 'Ferrari Enzo', 'supercar', 'Флагманский суперкар, представленный в 2002 году, был посвящён основателю марки Энцо Феррари. Купе комплектовали шести-литровым безнаддувным V12, развивавшим 660 л.с. и 658 Нм крутящего момента. До 100 кмч Ferrari Enzo могла разогнаться за 3,6 секунды при максимальной скорости более 350 кмч.', 'ferrarienzo', false, 0, 120000);
INSERT INTO public.car (id, car_name, category, description, icon_name, in_rent, number_of_rents, price) VALUES (9, 'Nissan GT-R 32', 'sportcar', 'Рожденный как суперкар, Skyline GT-R в кузове BNR32 с годами почти не потерял в цене. Настаивался, будто вино, и в итоге в Японии сейчас стоит лишь более чем вполовину дешевле, нежели 30 лет назад. ', 'gtr32', true, 1, 15000);
INSERT INTO public.car (id, car_name, category, description, icon_name, in_rent, number_of_rents, price) VALUES (5, 'Chevy Camaro', 'american_muscle', 'Среди положительных качеств спорткара – привлекательная внешность, мощные двигатели, хорошая динамика, надежная конструкция', 'camaro', false, 1, 10000);
INSERT INTO public.car (id, car_name, category, description, icon_name, in_rent, number_of_rents, price) VALUES (18, 'McLaren 570s', 'supercar', 'Стремления предназначены для тех, кто надеется добиться больших успехов; McLaren 570S 2020 года предназначен для тех, кто действительно это делает. Не заблуждайтесь, этот спортивный автомобиль со средним расположением двигателя предназначен не только для того, чтобы колесить по бульвару или привлекать зевак, где бы он ни появился. ', 'mclaren', false, 0, 98000);
